import React from 'react' 

const Alert = ({txtMainHeading,txtSubHeading}) => {
    return (
        <div>
            alert
            <h4>{txtMainHeading}</h4>
            <h6>{txtSubHeading}</h6>
        </div>
    )
}


export {
    Alert
}

import React, { useState } from 'react'
import styles from './styles.module.css'

import { Alert } from './components'

export const ReactInternetSpeedMeter = ({ 
  txtMainHeading="Opps..." ,
  outputType="alert",
  txtSubHeading="Diconnected from internet" }) => {

  const [ isOffline,setisOffline ] = useState(false)

  window.addEventListener('offline', ()=> setisOffline(true));
  window.addEventListener('online', ()=> setisOffline(false));

  if(isOffline){
    if(outputType == 'alert'){
      return <Alert 
                txtMainHeading={txtMainHeading} 
                txtSubHeading={txtSubHeading } />
    }

    return <div>Available "outputType" are alert, model</div>
  }
  return <div>online</div>
}
